Script.Body:
function OnAfterTransform(platform,transformContext)
{
        if ( transformContext.Target != null )
        {
                var pathAttribute = transformContext.Target.GetAttribute("Project");
                if(pathAttribute == null)
                {
                        var path = getPartitionPath(platform,transformContext.Source);
                        transformContext.Target.SetValueText("Project",path);
                }
                else if (pathAttribute.Value == null)
                {
                        pathAttribute.Value = getPartitionPath(platform,transformContext.Source);
                }
        }
}
function getPartitionPath(platform, source)
{
    var partition = platform.GetPartition();
    return partition.GetPartitionPath(source.Key.PartitionId);
}

function OnAfterTransform(platform, transformContext) {
  var sourceEntity = transformContext.Source;
  var targetEntity = transformContext.Target;

  var criteria = platform.GetDataSource().CreateCriteria();
  var res = platform.GetDataSource().ExecuteLoadDefaults('kahua_AEC_RFI.RFIData', criteria);

  if (res.Entities.Count > 0)
  {         
    for (var a = 0; a < res.Entities[0].Attributes.Count; a++)
    {
        var found = false;
        var attr = res.Entities[0].Attributes[a];
        for (var t = 0; t < targetEntity.Attributes.Count; t++)
        {
          if (targetEntity.Attributes[t].Name == attr.Name)
          {
              found = true;
              break;
          }
        }
        if (!found)
        {
            targetEntity.AddAttribute(attr);
        }
    }
  } 

  transformAttributeWithLookup(platform, transformContext, 'CSICode', 'Code', 'kahua_AEC_CSICode.CSICodeLookupWithCode');
  transformAttributeWithLookup(platform, transformContext, 'Location', 'Description', 'kahua_Location.LocationLookupWithDescription');
}
